import { useState } from "react";
import Todo from "./components/Todo/Todo";
import UserCard from "./components/UserCard/UserCard";

function App() {
  const [users, setUsers] = useState([
    {
      name: "John",
      age: 20,
      path: "https://example.com/user/1",
    },
    {
      name: "John 1",
      age: 22,
      path: "https://example.com/user/2",
    },
    {
      name: "John 3",
      age: 25,
      path: "https://example.com/user/6",
    },
  ])


  const addUser = () => {
    const newUser = {
      name: "John 4",
      age: 26,
      path: "https://example.com/user/26" + '/' + users.length + 1,
    }

    setUsers([...users, newUser])
  }


  return (
    <div>
      {/* <Todo/> */}
      <button onClick={addUser}>Добавить пользователя</button>

      {users.map((user) => (
        <UserCard key={user.path} user={user} />
      ))}
    </div>
  );
}

export default App;
