import React from "react";
import TodoList from "../TodoList/TodoList";
import { useState } from "react";
import {clsx} from 'clsx'

import styles from './todo.module.css';

const newColors = [
   '#fff' ,
 '#000' ,
 '#ff7d7d' ,
 '#ffa805' ,
 '#cdff05' ,
 '#65ff05' ,
 '#05ffea' ,
 '#0569ff' ,
 '#ff05f7' ,
]


const Todo = () => {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleChange = (e) => {
    setInputValue(e.target.value)
  }

  const addTask = (e) => {
    e.preventDefault()

    if(!inputValue.trim()) return

    const newTask = {
        id: tasks.length + 1,
        text: inputValue,
        isComplete: false,
    }

    setTasks([...tasks, newTask])
    setInputValue('')
  }

  const onEdit = (id) => {
    const newTasks = tasks.map((item) => {
      if(item.id === id) {
        const newItem = {...item, isComplete: !item.isComplete}
        return newItem
      }

      return item
    })
    setTasks(newTasks)
  }

  const onDelete = (id) => {
    const newTasks = tasks.filter(item => item.id !== id)
    setTasks(newTasks)
  }

  console.log(tasks);



  return (
    <div className={styles.todo}>
      <form action="" className={styles.form}>
        <input type="text" placeholder="Введите задачу" onChange={handleChange} value={inputValue} />
        <button disabled={!inputValue.trim()} onClick={addTask}>Добавить</button>
      </form>



      <TodoList tasks={tasks} onDelete={onDelete} onEdit={onEdit} />
    </div>
  );
};

export default Todo;
