import React from "react";
import UserCard from "../UserCard/UserCard";

const UsersList = (props) => {
  const { users } = props;

  const useCard = (user) => {
    console.log(user);
  }

  return (
    <div onClick={useCard}>
      {users.map((user, index) => {
        return (
          <UserCard 
            key={index} 
            name={user.name} 
            lastname={user.lastname} 
            click={useCard}
          />
        );
      })}
    </div>
  );
};

export default UsersList;
