import React from "react";
import TodoList from "../TodoList/TodoList";
import { useState } from "react";

const Todo = () => {
  const [tasks, setTasts] = useState([
    {
        id: 1,
        text: "Сделать домашнее задание",
        isComplete: false
    },
    {
        id: 2,
        text: "Сделать домашнее задание 2",
        isComplete: false
    }
  ]);

  return (
    <div>
      <form action="">
        <input type="text" placeholder="Введите задачу" />
        <button>Добавить</button>
      </form>

      <TodoList tasks={tasks}/>
    </div>
  );
};

export default Todo;
