import { useState } from "react";
import TodoFilters from "./components/TodoFilters/TodoFilters";
import TodoForm from "./components/TodoForm";
import TodoList from "./TodoList";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [tasks, setTasks] = useState([
    { id: 1, text: "Задача 1", complete: false },
    { id: 2, text: "Задача 2", complete: false },
    { id: 3, text: "Задача 3", complete: true },
    { id: 4, text: "Задача 4", complete: false },
  ]);

  const filteredActive = tasks.filter((item) => !item.complete);
  const filteredCompeleted = tasks.filter((item) => item.complete);

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const onAddTask = () => {};

  return (
    <div>
      <TodoForm handleChange={handleChange} onAddTask={onAddTask} />

      <TodoFilters
        tasks={tasks}
        filteredActive={filteredActive}
        filteredCompeleted={filteredCompeleted}
      />

      <TodoList/>

    </div>
  );
}

export default App;
