import { useState } from "react";
import Todo from "./components/Todo/Todo";
import UserCard from "./components/UserCard/UserCard";
import TodoForm from "./components/TodoForm";

function App() {
  const [inputValue, setInputValue] = useState('')
  const [tasks, setTasks] = useState([
    { id: 1, text: 'Задача 1', complete: false },
    { id: 2, text: 'Задача 2', complete: false },
    { id: 3, text: 'Задача 3', complete: true },
    { id: 4, text: 'Задача 4', complete: false },
  ])

  const filteredActive = tasks.filter((item) => !item.complete)
  const filteredCompeleted = tasks.filter((item) => item.complete)

  const handleChange = (e) => {
    setInputValue(e.target.value)
  };

  const onAddTask = () => {}

  return (
    <div>
      <TodoForm handleChange={handleChange} onAddTask={onAddTask}/>

      <div>
        <img src="" alt="" />
        Все: {tasks.length}
      </div>
      <div>
        <img src="" alt="" />
        Активные: {filteredActive.length}
      </div>

      <div>
        <img src="" alt="" />
        Активные: {filteredCompeleted.length}
      </div>


    </div>
  );
}

export default App;
