import React, { useState } from 'react'
import styles from "./ToDoList.module.css"
import ListItem from "./ListItem";

const List = ({tasks, onDelete, onEdit, activeTasks, finishedTasks, activeBtn}) => {
    let [activeBtnn, setActiveBtn] = useState(activeBtn)
  return (
    <ul className={styles.list}>
        {  activeBtnn === 1 && 
            tasks.map((item, index) => {
                return (
                    <div key={index}>
                        <ListItem onEdit={onEdit} onDelete={onDelete}  tasks={tasks} item={item}/>
                    </div>
                )
            })
        }
        {
            activeBtnn === 2 &&
            activeTasks.map((item, index) => {
                return (
                    <div key={index}>
                        <ListItem onEdit={onEdit} onDelete={onDelete}  tasks={tasks} item={item}/>
                    </div>
                )
            })
        }
        {
            activeBtnn === 3 &&
            finishedTasks.map((item, index) => {
                return (
                    <div key={index}>
                        <ListItem onEdit={onEdit} onDelete={onDelete}  tasks={tasks} item={item}/>
                    </div>
                )
            })
        }
    </ul>
  )
}

export default List
