import { useState } from "react"
import styles from "./TodoList.module.css"

function TodoList() {
    let [value, setValue] = useState("")
    let [tasks, setTasks] = useState([])
    function handleInputChange(e) {
        setValue(e.target.value.trim())
            
    }
    function createArr(e) {
        e.preventDefault()
        let newElementInArr = {
            id: tasks.length + 1,
            text: value,
            isCompleted: false,
        }
        setTasks([...tasks, newElementInArr])
        setValue("")
        
    }
    console.log(tasks);
    function onDelete(id) {
        let updateTasks = tasks.filter((item) => {
           return item.id !== id
        })
        setTasks(updateTasks)
    }
    function onFnished(id) {
        let newTasks = tasks.map((item) => {
            if(id === item.id) {
                let newItem = {...item, isCompleted: !isCompleted}
                return newItem
            }
            return item
        })
        setTasks(newTasks)
    }
    return (
        <div className={styles.card}>
            <form onSubmit={createArr} className={styles.form}>
                <input 
                type="text" 
                onChange={handleInputChange}
                value={value}
                />
                <button className={styles.add_btn} disabled={value === ""} type="submit">Добавить</button>
            </form>
            <ul className={styles.list}>
                {
                    tasks.map((item) => {
                        return (
                            <div key={item.id} className={styles.list_item}>
                                <li>{item.text}</li>
                                <div className={styles.btn_cont}>
                                    <button onClick={() => onDelete(item.id)} className={styles.delete_btn}>Delete</button>
                                    <button onClick={() => onFnished(item.id)} className={styles.finished_btn}>Finished</button>
                                </div>
                            </div>
                        )
                    })
                }

            </ul>
        </div>
    )
}

export default TodoList
