import UseStateSixCss from "./UseStateSix.module.css"
import { useState } from "react"

function UseStateSix() {
    let [state, setState] = useState(false) 
    let arr = ["Включено", "Отключено"]
    let [text, setText] = useState(arr[0]) 
    function ChangeState() {
        setState(!state)
        setText(arr[Number(state)])
        console.log(state);
    }
    return (
        <>
        <div>
            <button onClick={ChangeState} disabled={state}>
                {text}
            </button>
        </div>
        </>
    )
    
}

export default UseStateSix
