import { useState } from "react"
import styles from "./TipCalculator.module.css"
import { click } from "@testing-library/user-event/dist/click";


const TipCalculator = (props) => {
  let [sum, setSum] = useState(0)
  let [procent, setProcent] = useState("")
  function handleInputValue1(e) {
    let sum = +(e.targer.value)
    let sum1 = sum * procent
    function click() {
      setSum(sum + sum1)
    }
  } 

  function handleInputValue2(e) {
    setProcent(+(e.targer.value) / 100)
  } 
  return (
    <div className={styles.card}>
      <input 
      onChange={handleInputValue1}
      type="text" 
      placeholder="Введи сумму"
      />
      <input 
      onChange={handleInputValue2}
      type="text"
      placeholder="Введи прочент чаевых" 
      />
      <button onClick={click}>Посчитать</button>
    </div>
  );
};

export default TipCalculator;
