import UseStateTenCss from "./UseStateTen.module.css"
import { useState } from "react"

function UseStateTen() {
    let [state, setState] = useState(false) 
    function Activet() {
        setState(!state)
    }
    return (
        <>
        <button onClick={Activet}>Показать</button>
        <span>
        {
            (state && (
                "Добро Пожаловать"
            ))
        }
        </span>
        </>
    )
    
}

export default UseStateTen
