import { useState } from "react"
import styles from "./Tabs.module.css"


function Tabs () {
    let [count, setCount] = useState(0)
    function funCount() {
        setCount(++count)
    }
    return (
        <div className={`${styles.card} `}>  
            <button onClick={funCount}>Лайк</button>
            <span>{count} лайков</span>
        </div>
    )
}

export default Tabs
