import { useState } from "react"
import styles from "./CountdownTimer.module.css"


function CountdownTimer () {
    let [timer, setTimer] = useState(60)
    function funTimer() {
        setInterval(() => {
            setTimer(--timer)
        })
    }
    return (
        <div className={`${styles.card} `}>   
            <button>start</button>
            <span>60</span>
        </div>
    )
}

export default CountdownTimer
