import { useState } from "react"

function UseStateNine() {
    let [state, setState] = useState(false) 
    function Activet() {
        setState(!state)
    }
    return (
        <>
        <button>Показать</button>
        {
            (state && (
                "Добро Пожаловать"
            ))
        }
        </>
    )
    
}

export default UseStateNine
